define(['jquery', 'jquery/validate', 'mage/translate'], function($) {
    "use strict";
    return function() {
        $.validator.addMethod('validate-phone-strict', function(value) {
            console.log(value.substring(0, 1) != 0);
            return value.length > 9 && value.length < 11 && value.match(/^\d{10}$/) && value.substring(0, 1) == 0;
        }, $.mage.__("Please specify a valid mobile phone number consisting of 10 digits and starting with 0"));
    };
})