define(['jquery', 'Magento_Ui/js/modal/modal', 'jquery/validate', 'intlTelInput', 'intlTelInputUtils'], function($, modal) {
    'use strict';

    function changeLoginUser(userInput, userName) {
        $('.sparsh-user-name.sparsh-mobile-number').hide();
        userInput.click(function() {
            let inputId = $(this).attr('id');
            let inputValue = $(this).attr('value');
            if (inputId == "email_user" || inputId == "email_user_login") {
                inputValue = "sparsh-email";
                if (window.isEnable != 0) {
                    $('.form.password.forget').attr('action', window.urlForgotPass);
                    $('#popup_login-form').attr('action', window.actionLoginPost);
                    $(document).find('.sparsh-password').show();
                }
            }
            if (inputId == "mobile_number_user" || inputId == "mobile_number_user_login") {
                inputValue = "sparsh-mobile-number";
                if (window.isEnable != 0) {
                    $(document).find('.sparsh-password').hide();
                    $('#popup_login-form').attr('action', window.actionNew);
                    $('.form.password.forget').attr('action', window.urlResetPassword);
                }
            }
            let targetValue = $("." + inputValue);
            let user = $('.sparsh-user-name');
            user.not(targetValue).hide();
            user.find('input').removeAttr('name');
            $(targetValue).show();
            $(targetValue).find('input').attr('name', userName);
        });
    }

    function changeMobileEmail() {
        $(document).on('click', '#send2', function() {
            var type = $('input[name="user_option"]:checked').val();
            if (type == "sparsh-mobile-number" && $('#popup_login-form').valid()) {
                $.ajax({
                    url: window.actionNew,
                    type: 'POST',
                    data: $('#popup_login-form').serializeArray(),
                    dataType: 'JSON',
                    showLoader: true,
                    success: function(res) {
                        showPopupOtp('.block-customer-opt');
                        $('.block-customer-opt').html(res.otplogin).modal("openModal");
                        $('#login-form-popup').modal("closeModal");
                    }
                });
                return false;
            } else {
                return true
            }
        })
    }

    function showPopupOtp(element) {
        var options = {
            type: 'popup',
            responsive: false,
            innerScroll: false,
            title: '',
            buttons: [],
            modalClass: 'modal-login otp',
        };
        modal(options, element);
        $(document).on('click', '#back-login-form', function() {
            $('#login-form-popup').modal("openModal");
            $('.block-customer-opt').modal("closeModal");
        });
    }

    function setCountryDropdown(telInput, countryCode) {
        let countryCodeValue = countryCode.val();
        telInput.intlTelInput({
            separateDialCode: true,
            autoPlaceholder: false,
            formatOnDisplay: false,
            preventInvalidNumbers: true,
            preferredCountries: [],
            initialCountry: $.trim(countryCodeValue) ? countryCodeValue : 'auto',
            geoIpLookup: function(success, failure) {
                success('VN');
            },
            utilsScript: intlTelInputUtils
        });
    }

    function validateMobileNumber(telInput, countryCode) {
        $.validator.addMethod('validate-mobile-number', function(value, element) {
            if ($.trim(telInput.val())) {
                if (telInput.intlTelInput('isValidNumber')) {
                    countryCode.val(telInput.intlTelInput('getSelectedCountryData').iso2);
                    return true;
                }
                return false;
            }
        }, $.mage.__('Please enter a valid mobile number.'));
    }
    return {
        changeMobileEmail: changeMobileEmail,
        changeLoginUser: changeLoginUser,
        setCountryDropdown: setCountryDropdown,
        validateMobileNumber: validateMobileNumber
    };
});